import React, { useState, useMemo } from "react";
import "./App.css";

function App() {
  const [counter, setCounter] = useState(0);
  const [persons, setPersons] = useState([]);
  const [newPerson, setNewPerson] = useState("");

  const incrementCounter = () => {
    setCounter((prevCounter) => prevCounter + 1);
  };
  const addPerson = () => {
    if (newPerson.length === 0) {
      return;
    }
    setPersons((prevPersons) => [...prevPersons, newPerson]);
  };

  const handleChange = (event) => {
    setNewPerson(event.target.value);
  };

  const getHowManyPersons = () => {
    console.log("getHowManyPersons runs");
    return persons.length;
  };

  const calculation = getHowManyPersons();

  return (
    <div className="App">
      <section>
        <h2>Persons</h2>
        <ol>
          {persons.map((person, index) => {
            return <li key={index}>{person}</li>;
          })}
        </ol>
        <button onClick={addPerson}>Add Person</button>
      </section>
      <section>
        <button onClick={incrementCounter}>increment</button>
        <p>Counter: {counter}</p>
      </section>
      <section>
        <label htmlFor="newPerson">New Person</label>
        <input
          type="text"
          id="newPerson"
          name="newPerson"
          onChange={handleChange}
          value={newPerson}
        />
      </section>
      <h2>
        {`There ${persons.length === 1 ? "is" : "are"} ${calculation}
        ${persons.length === 1 ? "person" : "persons"}`}
      </h2>
    </div>
  );
}

export default App;
